var voiceList = document.getElementById("voiceList")
let synth = speechSynthesis;

voices();

function voices(){
    for(let voice of synth.getVoices()){
        let selected = voice.name === "Microsoft David - English (United States) (en-US)" ? "selected" : "";
        let option = `<option value="${voice.name}" ${selected}>${voice.name} (${voice.lang})</option>`;
        voiceList.insertAdjacentHTML("beforeend", option);
    }
}

synth.addEventListener("voiceschanged", voices);


function textToSpeech(text){
    let utterance = new SpeechSynthesisUtterance(text);
    for(let voice of synth.getVoices()){
        if(voice.name === voiceList.value){
            utterance.voice = voice;
        }
    }
    synth.speak(utterance);
}

const detect = document.querySelector("#detect");
const wrapper = document.querySelector(".banner");
const button = wrapper.querySelector(".fa-times");
let adClasses = ["ad", "ads", "adsbox", "doubleclick", "ad-placement", "ad-placeholder", "adbadge", "BannerAd"];
for(let item of adClasses){
    detect.classList.add(item);
  }
      let getProperty = window.getComputedStyle(detect).getPropertyValue("display");
      if(!wrapper.classList.contains("show")){
        getProperty == "none" ? wrapper.classList.add("show") : wrapper.classList.remove("show");
      }
button.addEventListener("click", ()=>{
    wrapper.classList.remove("show");
  });
